        .merchant.cwa > div {width: calc(100% - 20px);}
        /* card with arrow ] */

        /* review [ */
        .review__wrapper {
            margin-top: var(--size-xl);
        }
        .review, .qna__item {
            padding: var(--size-m) 0;
        }
        .review__amt {line-height: 2.6rem;}
        .review__empty-message {
            font-size: 16px;
