            width: 88px;
            height: 28px;
            margin-top: -60px;
            margin-left: auto;
            display: grid;
            place-content: center left;
            font-size: var(--size-l);
            font-weight: bold;
            background: white var(--star) no-repeat 66px 9px;
            background-size: 38px;
        }
        .review__head {
            display: flex;
            justify-content: space-between;
        }
        .review__head-right, .like {
            line-height: 1;
            display: flex;
            align-items: center;
        }
        .review__content {color: var(--black-medium);}
